mapper.select:
SELECT IDX as arId, AR_NM as arName, (SELECT ITEM_NM FROM `AVP`.`AVP_ITEM_TB` WHERE IDX = ITEM_ID) as itemName, DATE_FORMAT(CREATE_DTTM,'%Y-%m-%d %H:%i:%s') as createDate FROM `AVP`.`AVP_AR_TB` WHERE (DEL_YN = 'N' OR DEL_YN is NULL)
ORDER BY IDX DESC
LIMIT #{pageInfo.startRowNum}, #{pageInfo.size}

SELECT IDX as arId, AR_NM as arName, (SELECT ITEM_NM FROM `AVP`.`AVP_ITEM_TB` WHERE IDX = ITEM_ID) as itemName, DATE_FORMAT(CREATE_DTTM,'%Y-%m-%d %H:%i:%s') as createDate FROM `AVP`.`AVP_AR_TB` WHERE COMPANY_ID = #{companyId} AND (DEL_YN = 'N' OR DEL_YN is NULL)
ORDER BY IDX DESC
LIMIT #{pageInfo.startRowNum}, #{pageInfo.size}

SELECT TTA.TARGET_NAME as targetName , TTA.AR_ID as arId , TTS.SHOP_NAME as shopName , DATE_FORMAT(TTA.CREATE_DATE,'%Y-%m-%d') as createDate FROM `${dbName}`.`tb_tour_ar` TTA JOIN `${dbName}`.`tb_tour_shop` TTS ON TTA.SHOP_ID = TTS.SHOP_ID WHERE TTA.CREATE_ID = #{userId}
ORDER BY TTA.AR_ID DESC
LIMIT #{pageInfo.startRowNum}, #{pageInfo.size}

SELECT COUNT(*)
FROM `${dbName}`.`tb_tour_ar`
WHERE	CREATE_ID	=	#{userId}	AND	AR_ID	=	#{arId}
